Kiểm tra một số nguyên là số dương, âm hay không?
#include &lt;iostream&gt;
using namespace std;

int main()
{
    int n;
    cout &lt;&lt; "Nhap vao so nguyen = ";
    cin &gt;&gt; n;
    if (n &gt; 0) goto nhan1;
    if (n &lt; 0) goto nhan2;
    if (n == 0) goto nhan3;
nhan1:
    cout &lt;&lt; "so duong";
    goto nhan;
nhan2:
    cout &lt;&lt; "so am";
    goto nhan;
nhan3:
    cout &lt;&lt; "so khong";
nhan:
    return 0;
}



