#include &lt;iostream&gt;
#include &lt;string&gt;
#include &lt;algorithm&gt; // Để dùng std::reverse

// Hàm đảo ngược chuỗi, trả về một chuỗi mới
std::string DaoChuoi(const std::string& s1) 
{ 
    std::string s2 = s1; // Sao chép chuỗi ban đầu
    std::reverse(s2.begin(), s2.end()); // Dùng thuật toán đảo ngược
    return s2;
}

int main() 
{
    std::string s1, s2; 
    std::cout &lt;&lt; "Nhap vao chuoi ky tu: "; 
    std::getline(std::cin, s1); 

    s2 = DaoChuoi(s1); 

    std::cout &lt;&lt; "Ket qua sau khi dao nguoc chuoi: " &lt;&lt; s2 &lt;&lt; std::endl;
    return 0;
}

