// Giả sử MANG2D đã được định nghĩa
int TimMin(MANG2D a, int d, int c) 
{ 
    int min = a[0][0]; 
    for(int i = 0; i &lt; d; i++)
    {
        for(int j = 0; j &lt; c; j++) 
        {
            if(a[i][j] &lt; min) 
            {
                min = a[i][j];
            }
        }
    }
    return min; 
} 



